```typescript
async function main() {
    const serviceLocator = ServiceLocator.$;

    const appService = new AppService();
    const uiAdapterService = new UIAdapterService();
    const channelService = new ChannelService();
    const directMessageService = new DirectMessageService();
    const dynamicChannelService = new DynamicChannelService();
    const masterChannelService = new MasterChannelService();

    serviceLocator.register("AppService", appService);
    serviceLocator.register("UIAdapterService", uiAdapterService);
    serviceLocator.register("ChannelService", channelService);
    serviceLocator.register("DirectMessageService", directMessageService);
    serviceLocator.register("DynamicChannelService", dynamicChannelService);
    serviceLocator.register("MasterChannelService", masterChannelService);

    await Promise.all([
        appService.initialize(),
        uiAdapterService.initialize(),
        channelService.initialize(),
        directMessageService.initialize(),
        dynamicChannelService.initialize(),
        masterChannelService.initialize(),
    ]);
}

main().catch(console.error);
