type TServiceNameDependencies<T> = {
    [K in keyof T]: TServiceName;
}

type NonEmptyObject<T> = keyof T extends never ? never : T;

abstract class ServiceDependenciesAbstract<T extends { [key: string]: ServiceBase } = any> extends ObjectBase {
    private services: T = {} as T;

    public static getName() {
        return "VertixBase/Modules/Service/ServiceDependenciesAbstract";
    }

    public constructor(private dependencies: NonEmptyObject<TServiceNameDependencies<T>>) {
        super();
    }

    public getServices(): T {
        return this.services;
    }
}

abstract class ServiceBase<D extends { [key: string]: ServiceBase } = any> extends ObjectBase {
    declare protected services: Required<ServiceDependenciesAbstract<D>["services"]>;

    public static getName(): string {
        return "VertixBase/Modules/ServiceBase";
    }

    protected getDependencies(): ServiceDependenciesAbstract<D> | undefined {
        return undefined;
    }

    protected async initialize() {
        const dependencies = this.getDependencies();

        if (dependencies) {
            this.services = dependencies.getServices() as Required<typeof dependencies.getServices>;
        }
    }
}

