            channelName = newState.channel?.name as string;

        this.logger.log(this.onLeave,
            `Guild id: '${oldState.guild.id}' - User: '${displayName}' left channel guild: '${oldState.guild.name}'`
        );

        const leaveArgs: IChannelLeaveGenericArgs = {
            oldState,
            newState,
            displayName,
            channelName
        };

        if (oldState.channelId && await ChannelModel.$.isDynamic(oldState.channelId)) {
            eventBus.emit('leaveDynamicChannel', leaveArgs);
        }
    }

    private async onJoinMasterChannel(args: IChannelEnterGenericArgs) {
        // Handle join master channel logic
    }

    private async onJoinDynamicChannel(args: IChannelEnterGenericArgs) {
        // Handle join dynamic channel logic
    }

    private async onLeaveDynamicChannel(args: IChannelLeaveGenericArgs) {
        // Handle leave dynamic channel logic
