```typescript
// eventbus.spec.ts
import { EventBus } from "@vertix.gg/base/src/modules/eventbus/eventbus";
import { ObjectBase } from "@vertix.gg/base/src/bases/object-base";

class MockObject extends ObjectBase {
    public static getName() {
        return "MockObject";
    }

    public mockMethod() {
        console.log("Mock method called");
    }
}

describe("Modules/Eventbus", () => {
    let eventbus: EventBus;
    let mockObject: MockObject;
    let mockCallback: jest.Mock;

    beforeEach(() => {
        eventbus = EventBus.getInstance();
        mockObject = new MockObject();
        mockCallback = jest.fn();
    });

    it("should register and unregister objects", () => {
        eventbus.register(mockObject, ["mockMethod"]);
        eventbus.unregister(mockObject.getName());

        // No assertions needed, just ensure no errors are thrown
    });

    it("should throw an error when registering an already registered object", () => {
        eventbus.register(mockObject, ["mockMethod"]);
        expect(() => eventbus.register(mockObject, ["mockMethod"])).toThrow();
    });

    it("should emit events and trigger callbacks", () => {
        eventbus.register(mockObject, ["mockMethod"]);
        eventbus.on(`${mockObject.getName()}.mockMethod`, mockCallback);

        mockObject.mockMethod();

        expect(mockCallback).toHaveBeenCalled();
    });

    it("should remove event listeners when unregistering an object", () => {
        eventbus.register(mockObject, ["mockMethod"]);
        eventbus.on(`${mockObject.getName()}.mockMethod`, mockCallback);

        eventbus.unregister(mockObject.getName());
        mockObject.mockMethod();

        expect(mockCallback).not.toHaveBeenCalled();
    });
});
