    export const badwordsSomeUsed = ( content: string, badwords: string[] ): string | null => {
        for ( const badword of badwords ) {
            let words = content.split( " " );

            if ( ! words.length ) {
                words = [ content ];
            }

            for ( const word of words ) {
                if ( isMatch( word, badword ) ) {
                    return badword;
                }
            }
        }
        return null;
    }
    ```

