    });

    afterEach(() => {
        jest.clearAllMocks();
    });

    describe("generateCustomId", () => {
        it("should handle big length", () => {
            const id = "VertixBot/UI-V2/SetupAdapter:VertixBot/UI-V2/SetupMasterEditButton:0";

            console.log("Hash Table Before:", uiManager.hashTable);

            const hash = uiManager.generateCustomId(id, ":", 100);

            console.log("Hash Table After:", uiManager.hashTable);

